class Solution:
    def longestPalindrome(self, s: str) -> int:
        counts = {}
        for char in s:
            counts[char] = counts.get(char, 0) + 1

        usable_letters = 0
        odd = False
        for char in counts:
            count = counts[char]
            usable_letters += count
            if count % 2 != 0:
                odd = True
                usable_letters -= 1
        return usable_letters + (1 if odd else 0)


if __name__ == '__main__':
    solution = Solution()
    print(solution.longestPalindrome(
        ""))
